import React, { useState } from 'react'

const ReactRotatingText = ({ color, cursor, className }) => {
  const [output] = useState(``)

  return (
    <span className={className} style={{ color }}>
      {output}
      {cursor && <span className="react-rotating-text-cursor">|</span>}
    </span>
  )
}

